import React, { createContext, useContext, useState, useEffect } from 'react';
import { User } from '@shared/schema';
import { apiRequest } from './queryClient';

interface AuthContextType {
  user: User | null;
  token: string | null;
  login: (email: string, password: string) => Promise<User>;
  logout: () => void;
  isAdmin: boolean;
  isGuest: boolean;
}

const AuthContext = createContext<AuthContextType | null>(null);

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [token, setToken] = useState<string | null>(null);

  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');

    if (storedToken && storedUser) {
      setToken(storedToken);
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const login = async (email: string, password: string) => {
    try {
      const response = await apiRequest('POST', '/api/auth/login', { email, password });
      const data = await response.json();

      setUser(data.user);
      setToken(data.token);

      localStorage.setItem('token', data.token);
      localStorage.setItem('user', JSON.stringify(data.user));

      return data.user;
    } catch (error) {
      throw error;
    }
  };

  const logout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  };

  const value = {
    user,
    token,
    login,
    logout,
    isAdmin: user?.role === 'admin',
    isGuest: user?.role === 'guest',
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

client/src/lib/currency.ts.content:
export type Currency = 'INR' | 'BHD';

export const CURRENCY_SYMBOLS = {
  INR: '‚Çπ',
  BHD: 'BD',
} as const;

export const CURRENCY_NAMES = {
  INR: 'Indian Rupees',
  BHD: 'Bahrain Dinar',
} as const;

export function formatPrice(amount: string | number, currency: Currency): string {
  const symbol = CURRENCY_SYMBOLS[currency];
  const numAmount = typeof amount === 'string' ? parseFloat(amount) : amount;

  if (currency === 'BHD') {
    return `${symbol} ${numAmount.toFixed(3)}`;
  }

  return `${symbol} ${numAmount.toLocaleString('en-IN', { minimumFractionDigits: 0, maximumFractionDigits: 0 })}`;
}

export function parsePrice(priceStr: string): number {
  return parseFloat(priceStr.replace(/[^\d.-]/g, ''));
}

export function formatCurrency(amount: number, currency: Currency): string {
  return formatPrice(amount, currency);
}

