import { useState } from 'react';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import { Badge } from '@/components/ui/badge';
import { FileText, Eye } from 'lucide-react';
import { Product, BillItem } from '@shared/schema';
import { Currency, formatPrice } from '@/lib/currency';
import { useAuth } from '@/lib/auth';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface BillingFormProps {
  currency: Currency;
  products: Product[];
}

export default function BillingForm({ currency, products }: BillingFormProps) {
  const { token } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [customerData, setCustomerData] = useState({
    customerName: '',
    customerEmail: '',
    customerPhone: '',
    customerAddress: '',
    currency: currency,
    makingCharges: '12.0',
    gst: '3.0',
    vat: '10.0', // Bahrain VAT - 10% standard rate
  });

  const [selectedProducts, setSelectedProducts] = useState<Map<string, { product: Product; quantity: number }>>(new Map());

  const createBillMutation = useMutation({
    mutationFn: async (billData: any) => {
      return apiRequest('POST', '/api/bills', billData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/bills'] });
      toast({
        title: "Success",
        description: "Bill created successfully!",
      });
      resetForm();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create bill.",
        variant: "destructive",
      });
    },
  });

  const handleProductSelection = (productId: string, checked: boolean) => {
    const newSelection = new Map(selectedProducts);

    if (checked) {
      const product = products.find(p => p.id === productId);
      if (product) {
        newSelection.set(productId, { product, quantity: 1 });
      }
    } else {
      newSelection.delete(productId);
    }

    setSelectedProducts(newSelection);
  };

  const handleQuantityChange = (productId: string, quantity: number) => {
    const newSelection = new Map(selectedProducts);
    const item = newSelection.get(productId);

    if (item) {
      newSelection.set(productId, { ...item, quantity: Math.max(1, quantity) });
      setSelectedProducts(newSelection);
    }
  };

  const calculateBillTotals = () => {
    let subtotal = 0;

    const billItems: BillItem[] = Array.from(selectedProducts.values()).map(({ product, quantity }) => {
      const price = customerData.currency === 'INR' ? parseFloat(product.priceInr) : parseFloat(product.priceBhd);
      const itemTotal = price * quantity;
      subtotal += itemTotal;

      const makingChargesAmount = (itemTotal * parseFloat(customerData.makingCharges)) / 100;

      let gstAmount = 0;
      let vatAmount = 0;
      let finalItemTotal = itemTotal + makingChargesAmount;

      if (customerData.currency === 'INR') {
        // India GST calculation
        gstAmount = ((itemTotal + makingChargesAmount) * parseFloat(customerData.gst)) / 100;
        finalItemTotal += gstAmount;
      } else if (customerData.currency === 'BHD') {
        // Bahrain VAT calculation
        vatAmount = ((itemTotal + makingChargesAmount) * parseFloat(customerData.vat)) / 100;
        finalItemTotal += vatAmount;
      }

      return {
        productId: product.id,
        productName: product.name,
        quantity,
        priceInr: product.priceInr,
        priceBhd: product.priceBhd,
        grossWeight: product.grossWeight,
        netWeight: product.netWeight,
        makingCharges: makingChargesAmount.toFixed(2),
        discount: '0',
        sgst: customerData.currency === 'INR' ? (gstAmount / 2).toFixed(2) : '0',
        cgst: customerData.currency === 'INR' ? (gstAmount / 2).toFixed(2) : '0',
        vat: customerData.currency === 'BHD' ? vatAmount.toFixed(2) : '0',
        total: finalItemTotal.toFixed(2),
      };
    });

    const makingChargesTotal = (subtotal * parseFloat(customerData.makingCharges)) / 100;
    let gstTotal = 0;
    let vatTotal = 0;
    let total = subtotal + makingChargesTotal;

    if (customerData.currency === 'INR') {
      gstTotal = ((subtotal + makingChargesTotal) * parseFloat(customerData.gst)) / 100;
      total += gstTotal;
    } else if (customerData.currency === 'BHD') {
      vatTotal = ((subtotal + makingChargesTotal) * parseFloat(customerData.vat)) / 100;
      total += vatTotal;
    }

    return {
      billItems,
      subtotal: subtotal.toFixed(2),
      makingCharges: makingChargesTotal.toFixed(2),
      gst: gstTotal.toFixed(2),
      vat: vatTotal.toFixed(2),
      total: total.toFixed(2),
    };
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (selectedProducts.size === 0) {
      toast({
        title: "Error",
        description: "Please select at least one product.",
        variant: "destructive",
      });
      return;
    }

    const { billItems, subtotal, makingCharges, gst, vat, total } = calculateBillTotals();

    const billData = {
      ...customerData,
      subtotal,
      makingCharges,
      gst,
      vat,
      discount: '0',
      total,
      paidAmount: total,
      paymentMethod: 'CASH',
      items: billItems,
    };

    createBillMutation.mutate(billData);
  };

  const resetForm = () => {
    setCustomerData({
      customerName: '',
      customerEmail: '',
      customerPhone: '',
      customerAddress: '',
      currency: currency,
      makingCharges: '12.0',
      gst: '3.0',
      vat: '10.0',
    });
    setSelectedProducts(new Map());
  };

  const totals = calculateBillTotals();

  return (
    <Card data-testid="card-billing-form">
      <CardHeader>
        <CardTitle className="flex items-center space-x-2">
          <FileText className="h-5 w-5" />
          <span>Create New Bill</span>
        </CardTitle>
      </CardHeader>

      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-6" data-testid="form-create-bill">
          {/* Customer Information */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-4">
              <h4 className="text-lg font-medium text-black">Customer Information</h4>

              <div>
                <Label htmlFor="customerName">Customer Name</Label>
                <Input
                  id="customerName"
                  value={customerData.customerName}
                  onChange={(e) => setCustomerData({ ...customerData, customerName: e.target.value })}
                  placeholder="Enter customer name"
                  required
                  data-testid="input-customer-name"
                />
              </div>

              <div>
                <Label htmlFor="customerEmail">Email</Label>
                <Input
                  id="customerEmail"
                  type="email"
                  value={customerData.customerEmail}
                  onChange={(e) => setCustomerData({ ...customerData, customerEmail: e.target.value })}
                  placeholder="customer@email.com"
                  required
                  data-testid="input-customer-email"
                />
              </div>

              <div>
                <Label htmlFor="customerPhone">Phone</Label>
                <Input
                  id="customerPhone"
                  type="tel"
                  value={customerData.customerPhone}
                  onChange={(e) => setCustomerData({ ...customerData, customerPhone: e.target.value })}
                  placeholder="+91 98765 43210"
                  required
                  data-testid="input-customer-phone"
                />
              </div>

              <div>
                <Label htmlFor="customerAddress">Address</Label>
                <Textarea
                  id="customerAddress"
                  value={customerData.customerAddress}
                  onChange={(e) => setCustomerData({ ...customerData, customerAddress: e.target.value })}
                  placeholder="Enter full address"
                  rows={3}
                  required
                  data-testid="textarea-customer-address"
                />
              </div>
            </div>

            {/* Bill Settings */}
            <div className="space-y-4">
              <h4 className="text-lg font-medium text-black">Bill Settings</h4>

              <div>
                <Label htmlFor="billCurrency">Currency</Label>
                <Select
                  value={customerData.currency}
                  onValueChange={(value: Currency) => setCustomerData({ ...customerData, currency: value })}
                >
                  <SelectTrigger data-testid="select-bill-currency">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="INR">Indian Rupees (INR)</SelectItem>
                    <SelectItem value="BHD">Bahrain Dinar (BHD)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="makingCharges">Making Charges (%)</Label>
                <Input
                  id="makingCharges"
                  type="number"
                  step="0.1"
                  value={customerData.makingCharges}
                  onChange={(e) => setCustomerData({ ...customerData, makingCharges: e.target.value })}
                  placeholder="12.0"
                  required
                  data-testid="input-making-charges"
                />
              </div>

              {customerData.currency === 'INR' && (
                <div>
                  <Label htmlFor="gst">GST (%)</Label>
                  <Input
                    id="gst"
                    type="number"
                    step="0.1"
                    value={customerData.gst}
                    onChange={(e) => setCustomerData({ ...customerData, gst: e.target.value })}
                    placeholder="3.0"
                    required
                    data-testid="input-gst"
                  />
                </div>
              )}

              {customerData.currency === 'BHD' && (
                <div>
                  <Label htmlFor="vat">VAT (%) - Bahrain</Label>
                  <Input
                    id="vat"
                    type="number"
                    step="0.1"
                    value={customerData.vat}
                    onChange={(e) => setCustomerData({ ...customerData, vat: e.target.value })}
                    placeholder="10.0"
                    required
                    data-testid="input-vat"
                  />
                </div>
              )}

              {/* Bill Summary */}
              {selectedProducts.size > 0 && (
                <div className="bg-gray-50 p-4 rounded-lg space-y-2">
                  <h5 className="font-medium text-black">Bill Summary</h5>
                  <div className="text-sm space-y-1">
                    <div className="flex justify-between">
                      <span>Subtotal:</span>
                      <span>{formatPrice(totals.subtotal, customerData.currency)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Making Charges ({customerData.makingCharges}%):</span>
                      <span>{formatPrice(totals.makingCharges, customerData.currency)}</span>
                    </div>
                    {customerData.currency === 'INR' && (
                      <div className="flex justify-between">
                        <span>GST ({customerData.gst}%):</span>
                        <span>{formatPrice(totals.gst, customerData.currency)}</span>
                      </div>
                    )}
                    {customerData.currency === 'BHD' && (
                      <div className="flex justify-between">
                        <span>VAT ({customerData.vat}%):</span>
                        <span>{formatPrice(totals.vat, customerData.currency)}</span>
                      </div>
                    )}
                    <div className="flex justify-between font-medium border-t pt-1">
                      <span>Total:</span>
                      <span>{formatPrice(totals.total, customerData.currency)}</span>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Product Selection */}
          <div>
            <h4 className="text-lg font-medium text-black mb-4">Select Products</h4>
            <div className="border border-gray-300 rounded-lg p-4 max-h-60 overflow-y-auto" data-testid="product-selection">
              {products.length === 0 ? (
                <p className="text-gray-500 text-center py-8">No products available.</p>
              ) : (
                products.map((product) => {
                  const isSelected = selectedProducts.has(product.id);
                  const selectedItem = selectedProducts.get(product.id);

                  return (
                    <div key={product.id} className="flex items-center justify-between p-3 border-b border-gray-200 last:border-b-0" data-testid={`product-option-${product.id}`}>
                      <div className="flex items-center space-x-3">
                        <Checkbox
                          checked={isSelected}
                          onCheckedChange={(checked) => handleProductSelection(product.id, checked as boolean)}
                          data-testid={`checkbox-product-${product.id}`}
                        />
                        <img
                          src={product.images[0] || "https://images.unsplash.com/photo-1603561596112-db2eca6c9df4?w=50"}
                          alt={product.name}
                          className="w-12 h-12 rounded object-cover"
                        />
                        <div>
                          <p className="font-medium text-black">{product.name}</p>
                          <p className="text-sm text-gray-600">
                            {formatPrice(customerData.currency === 'INR' ? product.priceInr : product.priceBhd, customerData.currency)}
                          </p>
                          <div className="flex items-center space-x-2">
                            <Badge variant={product.stock > 5 ? "default" : "destructive"} className="text-xs">
                              Stock: {product.stock}
                            </Badge>
                          </div>
                        </div>
                      </div>

                      {isSelected && (
                        <div className="flex items-center space-x-2">
                          <Label className="text-sm text-gray-600">Qty:</Label>
                          <Input
                            type="number"
                            min="1"
                            max={product.stock}
                            value={selectedItem?.quantity || 1}
                            onChange={(e) => handleQuantityChange(product.id, parseInt(e.target.value) || 1)}
                            className="w-16 text-center"
                            data-testid={`input-quantity-${product.id}`}
                          />
                        </div>
                      )}
                    </div>
                  );
                })
              )}
            </div>
          </div>

          {/* Actions */}
          <div className="flex space-x-4">
            <Button
              type="submit"
              className="flex-1 bg-black text-white hover:bg-gray-800"
              disabled={createBillMutation.isPending || selectedProducts.size === 0}
              data-testid="button-create-bill"
            >
              <FileText className="h-4 w-4 mr-2" />
              {createBillMutation.isPending ? 'Creating...' : 'Generate Bill'}
            </Button>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/components/admin/product-form.content:
import { useState } from 'react';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Plus, Edit, Trash2, Upload } from 'lucide-react';
import { Product } from '@shared/schema';
import { Currency } from '@/lib/currency';
import { useAuth } from '@/lib/auth';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface ProductFormProps {
  currency: Currency;
}

export default function ProductForm({ currency }: ProductFormProps) {
  const { token } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: '',
    priceInr: '',
    priceBhd: '',
    grossWeight: '',
    netWeight: '',
    stock: ''
  });
  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);

  const { data: products = [] } = useQuery<Product[]>({
    queryKey: ['/api/products'],
    enabled: !!token,
  });

  const addProductMutation = useMutation({
    mutationFn: async (data: FormData) => {
      return apiRequest('POST', '/api/products', data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/products'] });
      toast({
        title: "Success",
        description: "Product added successfully!",
      });
      resetForm();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add product.",
        variant: "destructive",
      });
    },
  });

  const deleteProductMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest('DELETE', `/api/products/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/products'] });
      toast({
        title: "Success",
        description: "Product deleted successfully!",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete product.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const data = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      data.append(key, value);
    });

    selectedFiles.forEach(file => {
      data.append('images', file);
    });

    addProductMutation.mutate(data);
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      setSelectedFiles(Array.from(e.target.files));
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      category: '',
      priceInr: '',
      priceBhd: '',
      grossWeight: '',
      netWeight: '',
      stock: ''
    });
    setSelectedFiles([]);
    setEditingProduct(null);
  };

  const handleDeleteProduct = (id: string) => {
    if (confirm('Are you sure you want to delete this product?')) {
      deleteProductMutation.mutate(id);
    }
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
      {/* Add Product Form */}
      <Card data-testid="card-add-product">
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Plus className="h-5 w-5" />
            <span>Add New Product</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4" data-testid="form-add-product">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="name">Product Name</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  placeholder="Enter product name"
                  required
                  data-testid="input-product-name"
                />
              </div>

              <div>
                <Label htmlFor="category">Category</Label>
                <Select 
                  value={formData.category} 
                  onValueChange={(value) => setFormData({ ...formData, category: value })}
                  required
                >
                  <SelectTrigger data-testid="select-category">
                    <SelectValue placeholder="Select Category" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="RINGS">Rings üíç</SelectItem>
                    <SelectItem value="NECKLACES_CHAINS">Necklaces & Chains üìø</SelectItem>
                    <SelectItem value="EARRINGS">Earrings üå∏</SelectItem>
                    <SelectItem value="BRACELETS_BANGLES">Bracelets & Bangles üîó</SelectItem>
                    <SelectItem value="PENDANTS_LOCKETS">Pendants & Lockets ‚ú®</SelectItem>
                    <SelectItem value="MANGALSUTRA">Mangalsutra & Thali Chains üñ§</SelectItem>
                    <SelectItem value="NOSE_JEWELLERY">Nose Jewellery üëÉ</SelectItem>
                    <SelectItem value="ANKLETS_TOE_RINGS">Anklets & Toe Rings üë£</SelectItem>
                    <SelectItem value="BROOCHES_PINS">Brooches & Pins üéÄ</SelectItem>
                    <SelectItem value="KIDS_JEWELLERY">Kids Jewellery üßí</SelectItem>
                    <SelectItem value="BRIDAL_COLLECTIONS">Bridal & Special Collections üë∞</SelectItem>
                    <SelectItem value="MATERIAL_GEMSTONE">Shop by Material / Gemstone üíé</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <Label htmlFor="description">Product Description</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="Enter detailed description"
                rows={3}
                required
                data-testid="textarea-description"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="priceInr">Price (INR)</Label>
                <Input
                  id="priceInr"
                  type="number"
                  step="0.01"
                  value={formData.priceInr}
                  onChange={(e) => setFormData({ ...formData, priceInr: e.target.value })}
                  placeholder="0"
                  required
                  data-testid="input-price-inr"
                />
              </div>

              <div>
                <Label htmlFor="priceBhd">Price (BHD)</Label>
                <Input
                  id="priceBhd"
                  type="number"
                  step="0.001"
                  value={formData.priceBhd}
                  onChange={(e) => setFormData({ ...formData, priceBhd: e.target.value })}
                  placeholder="0.000"
                  required
                  data-testid="input-price-bhd"
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="grossWeight">Gross Weight (g)</Label>
                <Input
                  id="grossWeight"
                  type="number"
                  step="0.1"
                  value={formData.grossWeight}
                  onChange={(e) => setFormData({ ...formData, grossWeight: e.target.value })}
                  placeholder="0.0"
                  required
                  data-testid="input-gross-weight"
                />
              </div>

              <div>
                <Label htmlFor="netWeight">Net Weight (g)</Label>
                <Input
                  id="netWeight"
                  type="number"
                  step="0.1"
                  value={formData.netWeight}
                  onChange={(e) => setFormData({ ...formData, netWeight: e.target.value })}
                  placeholder="0.0"
                  required
                  data-testid="input-net-weight"
                />
              </div>
            </div>

            <div>
              <Label htmlFor="stock">Stock Quantity</Label>
              <Input
                id="stock"
                type="number"
                value={formData.stock}
                onChange={(e) => setFormData({ ...formData, stock: e.target.value })}
                placeholder="0"
                required
                data-testid="input-stock"
              />
            </div>

            <div>
              <Label htmlFor="images">Product Images</Label>
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-gray-400 transition-colors">
                <input
                  id="images"
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleFileChange}
                  className="hidden"
                  data-testid="input-images"
                />
                <label htmlFor="images" className="cursor-pointer">
                  <Upload className="h-8 w-8 text-gray-400 mx-auto mb-2" />
                  <p className="text-gray-600">Click to upload images or drag and drop</p>
                  <p className="text-sm text-gray-500">PNG, JPG up to 10MB each</p>
                </label>
              </div>

              {selectedFiles.length > 0 && (
                <div className="mt-4 grid grid-cols-2 md:grid-cols-4 gap-4" data-testid="preview-images">
                  {selectedFiles.map((file, index) => (
                    <div key={index} className="relative">
                      <img
                        src={URL.createObjectURL(file)}
                        alt={`Preview ${index + 1}`}
                        className="w-full h-20 object-cover rounded border"
                      />
                      <button
                        type="button"
                        onClick={() => setSelectedFiles(files => files.filter((_, i) => i !== index))}
                        className="absolute top-1 right-1 bg-red-500 text-white rounded-full w-5 h-5 text-xs flex items-center justify-center"
                        data-testid={`button-remove-image-${index}`}
                      >
                        √ó
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>

            <Button
              type="submit"
              className="w-full bg-black text-white hover:bg-gray-800"
              disabled={addProductMutation.isPending}
              data-testid="button-add-product"
            >
              {addProductMutation.isPending ? 'Adding...' : 'Add Product'}
            </Button>
          </form>
        </CardContent>
      </Card>

      {/* Product List */}
      <Card data-testid="card-product-inventory">
        <CardHeader>
          <CardTitle>Product Inventory</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4 max-h-96 overflow-y-auto">
            {products.length === 0 ? (
              <p className="text-gray-500 text-center py-8" data-testid="message-no-products">
                No products added yet.
              </p>
            ) : (
              products.map((product) => (
                <div key={product.id} className="bg-white rounded-lg p-4 border shadow-sm" data-testid={`item-product-${product.id}`}>
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <img
                        src={product.images[0] || "https://images.unsplash.com/photo-1603561596112-db2eca6c9df4?w=60"}
                        alt={product.name}
                        className="w-15 h-15 rounded-lg object-cover"
                      />
                      <div>
                        <h4 className="font-medium text-black">{product.name}</h4>
                        <div className="flex items-center space-x-2">
                          <Badge variant={product.stock > 5 ? "default" : product.stock > 0 ? "destructive" : "secondary"}>
                            Stock: {product.stock}
                          </Badge>
                          <Badge variant="outline">{product.category}</Badge>
                        </div>
                        <p className="text-sm text-gray-600">
                          ‚Çπ{parseInt(product.priceInr).toLocaleString('en-IN')} | BD {parseFloat(product.priceBhd).toFixed(3)}
                        </p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => setEditingProduct(product)}
                        data-testid={`button-edit-${product.id}`}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleDeleteProduct(product.id)}
                        disabled={deleteProductMutation.isPending}
                        data-testid={`button-delete-${product.id}`}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/ui/alert-dialog.ts.content:
import * as React from "react"
import * as AlertDialogPrimitive from "@radix-ui/react-alert-dialog"

import { cn } from "@/lib/utils"
import { buttonVariants } from "@/components/ui/button"

const AlertDialog = AlertDialogPrimitive.Root

const AlertDialogTrigger = AlertDialogPrimitive.Trigger

const AlertDialogPortal = AlertDialogPrimitive.Portal

const AlertDialogOverlay = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Overlay
    className={cn(
      "fixed inset-0 z-50 bg-black/80  data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
      className
    )}
    {...props}
    ref={ref}
  />
))
AlertDialogOverlay.displayName = AlertDialogPrimitive.Overlay.displayName

const AlertDialogContent = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Content>
>(({ className, ...props }, ref) => (
  <AlertDialogPortal>
    <AlertDialogOverlay />
    <AlertDialogPrimitive.Content
      ref={ref}
      className={cn(
        "fixed left-[50%] top-[50%] z-50 grid w-full max-w-lg translate-x-[-50%] translate-y-[-50%] gap-4 border bg-background p-6 shadow-lg duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%] sm:rounded-lg",
        className
      )}
      {...props}
    />
  </AlertDialogPortal>
))
AlertDialogContent.displayName = AlertDialogPrimitive.Content.displayName

const AlertDialogHeader = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col space-y-2 text-center sm:text-left",
      className
    )}
    {...props}
  />
)
AlertDialogHeader.displayName = "AlertDialogHeader"

const AlertDialogFooter = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-2",
      className
    )}
    {...props}
  />
)
AlertDialogFooter.displayName = "AlertDialogFooter"

const AlertDialogTitle = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Title>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Title>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Title
    ref={ref}
    className={cn("text-lg font-semibold", className)}
    {...props}
  />
))
AlertDialogTitle.displayName = AlertDialogPrimitive.Title.displayName

const AlertDialogDescription = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Description>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Description>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Description
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
))
AlertDialogDescription.displayName =
  AlertDialogPrimitive.Description.displayName

const AlertDialogAction = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Action>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Action>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Action
    ref={ref}
    className={cn(buttonVariants(), className)}
    {...props}
  />
))
AlertDialogAction.displayName = AlertDialogPrimitive.Action.displayName

const AlertDialogCancel = React.forwardRef<
  React.ElementRef<typeof AlertDialogPrimitive.Cancel>,
  React.ComponentPropsWithoutRef<typeof AlertDialogPrimitive.Cancel>
>(({ className, ...props }, ref) => (
  <AlertDialogPrimitive.Cancel
